import React from "react";

const PhoneField = ({ handleChange, values, errors }) => {
  return (
    <div>
      <label className="label" htmlFor="phone">Телефон:</label>
      <input
        type="tel"
        id="phone"
        name="phone"
        value={values.phone}
        onChange={handleChange}
      />
      {errors.phone && <p cl>{errors.phone}</p>}
    </div>
  );
};

export default PhoneField;
