import React, { useState } from "react";
import './App.css';

const EmojiVoting = () => {
  const [emojis, setEmojis] = useState([
    { emoji: "üòÉ", count: 0 },
    { emoji: "üòç", count: 0 },
    { emoji: "üòÇ", count: 0 },
    // Add more emojis here
  ]);

  const handleEmojiClick = (index) => {
    const updatedEmojis = [...emojis];
    updatedEmojis[index].count += 1;
    setEmojis(updatedEmojis);
  };

  const handleShowResultsClick = () => {
    const winningEmoji = emojis.reduce((prev, current) =>
      prev.count > current.count ? prev : current
    );
    alert(`The winning emoji is: ${winningEmoji.emoji}`);
  };

  return (
    <div>
      <h1>–í–∏–±–µ—Ä–∏ —É–ª—é–±–ª–µ–Ω–∏–π —Å–º–∞–π–ª</h1>
      <ul>
        {emojis.map((emoji, index) => (
          <li key={index}>
            {emoji.emoji} - {emoji.count} clicks
            <button className="btn vote-btn" onClick={() => handleEmojiClick(index)}>–≥–æ–ª–æ—Å—É–≤–∞—Ç–∏</button>
          </li>
        ))}
      </ul>
      <button className="btn res-btn" onClick={handleShowResultsClick}>–ü–æ–∫–∞–∑–∞—Ç–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∏</button>
    </div>
  );
};

export default EmojiVotingApp;
