import './App.css';
import React, { useState, useEffect } from "react";

const EmojiVoting = () => {
  const [emojis, setEmojis] = useState(() => {
    // Get emojis from local storage, or use default emojis with count set to 0
    const storedEmojis = localStorage.getItem("emojis");
    if (storedEmojis) {
      return JSON.parse(storedEmojis);
    } else {
      return [
        { emoji: "üòÉ", count: 0 },
        { emoji: "üòç", count: 0 },
        { emoji: "üòÇ", count: 0 },
        // Add more emojis here
      ];
    }
  });

  useEffect(() => {
    // Update local storage with current emojis whenever emojis change
    localStorage.setItem("emojis", JSON.stringify(emojis));
  }, [emojis]);

  const handleEmojiClick = (index) => {
    const updatedEmojis = [...emojis];
    updatedEmojis[index].count += 1;
    setEmojis(updatedEmojis);
  };

  const handleShowResultsClick = () => {
    const winningEmoji = emojis.reduce((prev, current) =>
      prev.count > current.count ? prev : current
    );
    alert(`The winning emoji is: ${winningEmoji.emoji}`);
  };

  return (
    <div>
      <h1>–í–∏–±–µ—Ä–∏ —É–ª—é–±–ª–µ–Ω–∏–π —Å–º–∞–π–ª</h1>
      <ul>
        {emojis.map((emoji, index) => (
          <li key={index}>
            <span>{emoji.emoji}</span>
            <p>{emoji.count} clicks</p>
            <button className='vote-btn' onClick={() => handleEmojiClick(index)}>–≥–æ–ª–æ—Å—É–≤–∞—Ç–∏</button>
          </li>
        ))}
      </ul>
      <button className='res-btn' onClick={handleShowResultsClick}></button>
    </div>
  );
};

export default EmojiVoting;
